import React, { Component } from "react";
import { Text, StyleSheet, View, ScrollView } from "react-native";
import H1 from "../../Texts/H1";
import InputField from "../../inputFields/InputField";
import { makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import MenuItem from "@material-ui/core/MenuItem";

const countries = [
  {
    value: "Canada",
    label: "Canada"
  },
  {
    value: "USA",
    label: "USA"
  },
  {
    value: "UK",
    label: "UK"
  }
];

const useStyles = makeStyles(theme => ({
  root: {
    "& .MuiTextField-root": {
      margin: theme.spacing(1),
      width: "25ch"
    }
  }
}));

export default function DeliveryInfo() {
  const classes = useStyles();
  const [country, setCountry] = React.useState("Canada");

  const handleChange = event => {
    setCountry(event.target.value);
  };
  return (
    <ScrollView>
      <H1 h1Text="Delivery Information" />
      <TextField
        id="select-country"
        select
        label="Country"
        value={country}
        onChange={handleChange}
        helperText="Please select your country"
        variant="outlined"
      >
        {countries.map(option => (
          <MenuItem key={option.value} value={option.value}>
            {option.label}
          </MenuItem>
        ))}
      </TextField>
      <InputField fieldLabel="Address" fieldName="address" />
      <InputField fieldLabel="Postcode" fieldName="postcode" />
      <InputField fieldLabel="Delivery Instruction" fieldName="" />
    </ScrollView>
  );
}

const styles = StyleSheet.create({});
